// components/Relationships.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Relationships = () => {
    const [users, setUsers] = useState([]);
    const [relationships, setRelationships] = useState([]);

    useEffect(() => {
        fetchUsers();
        fetchRelationships();
    }, []);

    const fetchUsers = async () => {
        const response = await axios.get('http://localhost:8000/users/');
        setUsers(response.data);
    };

    const fetchRelationships = async () => {
        const response = await axios.get('http://localhost:8000/relationships/');
        setRelationships(response.data);
    };

    const handleRelationship = async (user1Id, user2Id, relationship) => {
        await axios.post('http://localhost:8000/relationships/', {
            user1: user1Id,
            user2: user2Id,
            relationship,
        });
        fetchRelationships();
    };

    return (
        <div>
            <h1>Users</h1>
            <ul>
                {users.map((user) => (
                    <li key={user.id}>
                        {user.name} - {user.email}
                        {/* Add a button for relationships */}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Relationships;
